mapper.insert:
insert into item2(
	ITEM_ID, ITEM_NAME, PRICE, DESCRIPTION, PICTURE_URL, PICTURE_URL2
) values (
	seq_item2.nextval, #{itemName}, #{price}, #{description}, #{pictureUrl}, #{pictureUrl2}
)

mapper.select:
select ITEM_ID, ITEM_NAME, PRICE, DESCRIPTION, PICTURE_URL, PICTURE_URL2
from ITEM2
where item_id = #{itemId}

select picture_url
from item2
where item_id = #{itemId}

select picture_url2
from item2
where item_id = #{itemId}
